package tax.cute.mcpingplugin.util;

import java.io.*;
import java.util.concurrent.ThreadLocalRandom;

public class Util {
    public final static String MC_SRV = "_Minecraft._tcp.";
    public final static String MC_SERVER_DEFAULT_FAVICON_BASE64 = "";
    public final static String MENU = " [ MCPing ] " +
            "\n可获取MC(JE|BE)服务器的Motd" +
            "\n使用方法:/mcping [域名/IP]" +
            "\n支持Srv和中文域名" +
            "\n完整功能请见项目的README"
            ;

    public static String combineArgs(String s, int start) {
        String[] args = s.split(" ");
        String str = "";
        for (; start < args.length; start++) {
            str += args[start];
        }
        return str;
    }

    public static boolean isNum(String data) {
        try {
            Long.parseLong(data);
            return true;
        } catch (NumberFormatException e) {
            return false;
        }
    }

    public static boolean isBoolean(String data) {
        return data.toLowerCase().equals("true") || data.toLowerCase().equals("false");
    }

    public static String readText(String path) throws IOException {
        return readText(path, "UTF-8");
    }

    public static String readText(String path, String charsets) throws IOException {
        InputStream in = new FileInputStream(path);
        String text = new String(in.readAllBytes());
        in.close();
        return text;
    }

    public static int random(int Max, int Min) {
        return ThreadLocalRandom.current().nextInt(Min, Max);
    }

    public static String clearColorCode(String text) {
        if (text == null) return null;
        if (!text.contains("§")) return text;

        while (text.contains("§")) {
            int index = text.indexOf("§");
            if (index + 2 > text.length()) {
                break;
            }
            text = text.replace(text.substring(index, index + 2), "");
        }
        text = text.replace("§","");
        return text;
    }

}
